<template>
  <a class="button" :href="href"> Continue with Google </a>
</template>

<script>
export default {
  name: "ButtonGoogle",
  data() {
    return {
      googleURL: "https://accounts.google.com/o/oauth2/v2/auth",
      queryParams: {
        response_type: "code",
        scope: "email openid profile",
        redirect_uri: `${window.location.origin}/login/google/callback`,
        client_id: "246513009319-46l79cuibsbgq46fnvu8b2ito4kifaa3.apps.googleusercontent.com",
      },
    };
  },
  computed: {
    googleURLAndQueryParamsString() {
      const queryParamsString = Object.entries(this.queryParams)
        .map(([key, value]) => `${key}=${value}`)
        .join("&");

      return `${this.googleURL}?${queryParamsString}`;
    },
    href() {
      return encodeURI(this.googleURLAndQueryParamsString);
    },
  },
};
</script>

